            <TabsTrigger value="geography" className="text-xs">
              Geography
            </TabsTrigger>
            <TabsTrigger value="ownership" className="text-xs">
              Ownership
            </TabsTrigger>
            <TabsTrigger value="checksize" className="text-xs">
              Check Size
