import { useState } from "react";
import { cn } from "@/lib/utils";
import { Link, useLocation } from "wouter";
import { useFundContext } from "@/contexts/FundContext";
import { POVIcon } from "@/components/ui/POVLogo";
import { getNavigationItems } from './navigation-config';
import {
  ChevronDown,
  ChevronRight,
  Plus
} from "lucide-react";

interface SidebarProps {
  activeModule: string;
  onModuleChange: (_module: string) => void;
}

// Use feature-flag aware navigation
const navigationItems = getNavigationItems();

const chartCategories = [
  { id: 'basic', label: 'Basic Charts' },
  { id: 'statistical', label: 'Statistical' },
  { id: 'hierarchical', label: 'Hierarchical' },
  { id: 'flow', label: 'Flow Charts' },
  { id: 'advanced', label: 'Advanced' },
];

export default function Sidebar({ activeModule, onModuleChange }: SidebarProps) {
  const [isChartsExpanded, setIsChartsExpanded] = useState(false);
  const [isHovered, setIsHovered] = useState(false);
  const [_location, _setLocation] = useLocation();
  const { needsSetup, currentFund } = useFundContext();

  return (
    <aside
      className={`bg-white shadow-card border-r border-lightGray flex-shrink-0 flex flex-col transition-all duration-300 ease-in-out ${
        isHovered ? 'w-64' : 'w-16'
      }`}
      onMouseEnter={() => setIsHovered(true)}
      onMouseLeave={() => setIsHovered(false)}
    >
      <div className="p-3 border-b border-lightGray bg-charcoal">
        <div className="flex items-center mb-4">
          <div className="flex items-center justify-center w-10 h-10">
            <POVIcon variant="white" size="md" />
          </div>
          <div className={`ml-3 transition-opacity duration-300 ${isHovered ? 'opacity-100' : 'opacity-0 pointer-events-none'} overflow-hidden`}>
            <h1 className="font-inter font-bold text-lg text-white whitespace-nowrap">Updawg</h1>
            <p className="font-poppins text-xs text-slate-300 whitespace-nowrap">Fund Management</p>
          </div>
        </div>
        {currentFund && isHovered && (
          <div className="bg-charcoal/30 rounded-lg p-3 border border-beige/30 transition-all duration-300">
            <p className="font-poppins font-medium text-sm text-white truncate">
              {currentFund.name}
            </p>
            <p className="font-mono text-xs text-white/70 mt-1">
              ${(currentFund.size / 1000000).toFixed(0)}M Fund
            </p>
          </div>
        )}
      </div>

      <nav className="flex-1 p-2 overflow-y-auto custom-scrollbar bg-white">
        {needsSetup && isHovered && (
          <div className="bg-amber-50 border border-amber-200 rounded-lg p-3 mb-4 transition-all duration-300">
            <div className="flex items-center space-x-2 mb-2">
              <Plus className="h-4 w-4 text-amber-700" />
              <span className="font-poppins text-sm font-medium text-amber-800">Setup Required</span>
            </div>
            <p className="font-poppins text-xs text-amber-600 mb-3">Configure your fund to access all features</p>
            <Link href="/fund-setup">
              <button className="w-full bg-amber-600 text-white px-3 py-2 rounded-md text-sm font-medium hover:bg-amber-700 transition-all duration-200">
                Start Fund Setup
              </button>
            </Link>
          </div>
        )}

        <ul className="space-y-1">
          {navigationItems.map((item: any) => {
            // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access
            const Icon = item.icon;
            // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
            const isActive = activeModule === item.id;
            // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
            const isDisabled = needsSetup && item.id !== 'fund-setup';

            return (
              // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access
              <li key={item.id}>
                // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                <Link href={`/${item.id}`}>
                  <button
                    disabled={isDisabled}
                    // eslint-disable-next-line @typescript-eslint/no-unsafe-argument, @typescript-eslint/no-unsafe-member-access
                    onClick={() => !isDisabled && onModuleChange(item.id)}
                    // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access
                    title={!isHovered ? item.label : undefined}
                    className={cn(
                      "w-full flex items-center rounded-md transition-colors font-poppins relative group",
                      isHovered ? "space-x-3 px-3 py-2.5" : "justify-center p-2.5",
                      isDisabled
                        ? "text-charcoal/40 cursor-not-allowed bg-lightGray"
                        : isActive
                          ? "bg-beige/30 text-charcoal font-medium"
                          : "text-charcoal/70 hover:bg-lightGray"
                    )}
                  >
                    <Icon className="h-5 w-5 flex-shrink-0" />
                    {isHovered && (
                      <span className={cn("text-sm whitespace-nowrap", isActive && "font-medium")}>
                        // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                        // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                        // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                        {item.label}
                      </span>
                    )}

                    {/* Tooltip for collapsed state */}
                    {!isHovered && (
                      <div className="absolute left-full ml-2 px-2 py-1 bg-charcoal text-white text-xs rounded-md opacity-0 group-hover:opacity-100 transition-opacity duration-200 pointer-events-none whitespace-nowrap z-50">
                        // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                        // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                        // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                        {item.label}
                      </div>
                    )}
                  </button>
                </Link>
              </li>
            );
          })}
        </ul>

        {isHovered && (
          <div className="mt-6 pt-4 border-t border-lightGray">
            <button
              onClick={() => setIsChartsExpanded(!isChartsExpanded)}
              className="w-full flex items-center justify-between px-2 py-2 text-xs font-medium text-charcoal/70 uppercase tracking-wider hover:text-charcoal transition-colors"
            >
              <span>Chart Types</span>
              {isChartsExpanded ? (
                <ChevronDown className="h-4 w-4" />
              ) : (
                <ChevronRight className="h-4 w-4" />
              )}
            </button>

            {isChartsExpanded && (
              <ul className="mt-3 space-y-1">
                {chartCategories.map((category: any) => (
                  // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access
                  <li key={category.id}>
                    <button className="w-full text-left px-2 py-1 text-sm text-charcoal/70 hover:text-charcoal hover:bg-lightGray rounded-md transition-colors">
                      // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                      // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                      // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                      {category.label}
                    </button>
                  </li>
                ))}
              </ul>
            )}
          </div>
        )}
      </nav>
    </aside>
  );
}

/**
 * Capital Call Schedule Component
 *
 * Visual selector for capital call deployment patterns with bar chart preview.
 * Supports even, front-loaded, back-loaded, and custom schedules.
 *
 * Features:
 * - Radio button selector for schedule types
 * - Visual bar chart showing deployment curve
 * - Custom schedule input (when selected)
 * - Real-time validation and feedback
 */

import React from 'react';
import { Label } from '@/components/ui/label';
import { Input } from '@/components/ui/input';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { getSchedulePattern, type CapitalCallScheduleType } from '@/lib/capital-calculations';

// ============================================================================
// TYPES
// ============================================================================

export interface CapitalCallScheduleProps {
  scheduleType: CapitalCallScheduleType;
  investmentPeriod: number;
  customSchedule?: Array<{ year: number; percentage: number }>;
  onChange: (type: CapitalCallScheduleType, customSchedule?: Array<{ year: number; percentage: number }>) => void;
}

// ============================================================================
// COMPONENT
// ============================================================================

export function CapitalCallSchedule({
  scheduleType,
  investmentPeriod,
  customSchedule,
  onChange
}: CapitalCallScheduleProps) {
  // Get current schedule pattern for visualization
  const pattern = React.useMemo(() => {
    try {
      return getSchedulePattern(scheduleType, investmentPeriod, customSchedule);
    } catch {
      // eslint-disable-next-line @typescript-eslint/no-unsafe-return
      return new Array(investmentPeriod).fill(100 / investmentPeriod);
    }
  }, [scheduleType, investmentPeriod, customSchedule]);

  // Handle custom schedule input
  const updateCustomSchedule = (year: number, percentage: number) => {
    const newSchedule = [...(customSchedule || [])];
    const existingIndex = newSchedule.findIndex(item => item.year === year);

    if (existingIndex >= 0) {
      newSchedule[existingIndex] = { year, percentage };
    } else {
      newSchedule.push({ year, percentage });
    }

    onChange('custom', newSchedule);
  };

  // Calculate validation
  const customTotal = customSchedule?.reduce((sum, item) => sum + item.percentage, 0) || 0;
  const isCustomValid = Math.abs(customTotal - 100) < 0.01;

  return (
    <div className="space-y-6">
      {/* Schedule Type Selector */}
      <div>
        <Label className="font-poppins text-charcoal-700 mb-3 block">
          Capital Call Schedule Type
        </Label>
        <RadioGroup
          value={scheduleType}
          onValueChange={(value) => onChange(value as CapitalCallScheduleType)}
          className="grid grid-cols-2 gap-4"
        >
          <label
            htmlFor="even"
            className={`flex items-center space-x-3 p-4 rounded-lg border-2 cursor-pointer transition-colors ${
              scheduleType === 'even'
                ? 'border-pov-teal bg-pov-teal/5'
                : 'border-charcoal-200 hover:border-charcoal-300'
            }`}
          >
            <RadioGroupItem value="even" id="even" />
            <div>
              <div className="font-inter font-bold text-sm text-pov-charcoal">Even</div>
              <div className="text-xs text-charcoal-600 font-poppins">Equal distribution</div>
            </div>
          </label>

          <label
            htmlFor="front-loaded"
            className={`flex items-center space-x-3 p-4 rounded-lg border-2 cursor-pointer transition-colors ${
              scheduleType === 'front-loaded'
                ? 'border-pov-teal bg-pov-teal/5'
                : 'border-charcoal-200 hover:border-charcoal-300'
            }`}
          >
            <RadioGroupItem value="front-loaded" id="front-loaded" />
            <div>
              <div className="font-inter font-bold text-sm text-pov-charcoal">Front-Loaded</div>
              <div className="text-xs text-charcoal-600 font-poppins">Deploy faster early</div>
            </div>
          </label>

          <label
            htmlFor="back-loaded"
            className={`flex items-center space-x-3 p-4 rounded-lg border-2 cursor-pointer transition-colors ${
              scheduleType === 'back-loaded'
                ? 'border-pov-teal bg-pov-teal/5'
                : 'border-charcoal-200 hover:border-charcoal-300'
            }`}
          >
            <RadioGroupItem value="back-loaded" id="back-loaded" />
            <div>
              <div className="font-inter font-bold text-sm text-pov-charcoal">Back-Loaded</div>
              <div className="text-xs text-charcoal-600 font-poppins">Deploy slower early</div>
            </div>
          </label>

          <label
            htmlFor="custom"
            className={`flex items-center space-x-3 p-4 rounded-lg border-2 cursor-pointer transition-colors ${
              scheduleType === 'custom'
                ? 'border-pov-teal bg-pov-teal/5'
                : 'border-charcoal-200 hover:border-charcoal-300'
            }`}
          >
            <RadioGroupItem value="custom" id="custom" />
            <div>
              <div className="font-inter font-bold text-sm text-pov-charcoal">Custom</div>
              <div className="text-xs text-charcoal-600 font-poppins">Set your own pattern</div>
            </div>
          </label>
        </RadioGroup>
      </div>

      {/* Custom Schedule Input */}
      {scheduleType === 'custom' && (
        <div className="bg-charcoal-50 rounded-lg p-4 space-y-4">
          <div className="flex items-center justify-between">
            <Label className="font-inter font-bold text-sm text-pov-charcoal">
              Custom Schedule (% per year)
            </Label>
            <span
              className={`text-sm font-poppins ${
                isCustomValid ? 'text-success' : 'text-error'
              }`}
            >
              Total: {customTotal.toFixed(1)}% {isCustomValid ? 'âœ“' : '(must = 100%)'}
            </span>
          </div>

          <div className="grid grid-cols-5 gap-3">
            {Array.from({ length: investmentPeriod }, (_, i) => {
              const year = i + 1;
              const value = customSchedule?.find(item => item.year === year)?.percentage || 0;

              return (
                <div key={year}>
                  <Label className="text-xs font-poppins text-charcoal-600 mb-1">
                    Year {year}
                  </Label>
                  <Input
                    type="number"
                    min="0"
                    max="100"
                    step="0.1"
                    value={value || ''}
                    onChange={(e) => updateCustomSchedule(year, parseFloat(e.target.value) || 0)}
                    placeholder="0.0"
                    className="text-sm"
                  />
                </div>
              );
            })}
          </div>

          {!isCustomValid && (
            <p className="text-sm text-error font-poppins">
              Percentages must sum to 100%
            </p>
          )}
        </div>
      )}

      {/* Visual Bar Chart */}
      <div className="space-y-3">
        <Label className="font-inter font-bold text-sm text-pov-charcoal">
          Deployment Curve Preview
        </Label>
        <div className="bg-white rounded-lg border border-charcoal-200 p-4">
          <div className="flex items-end justify-between gap-2 h-40">
            {pattern.map((percentage, index) => {
              // eslint-disable-next-line @typescript-eslint/no-unsafe-argument
              const height = (percentage / Math.max(...pattern)) * 100;
              const year = index + 1;

              return (
