interface AllocationBucket {
  id: string;
  name: string;
  targetPercentage: number;
  allocatedAmount: number;
  color: string;
  category: 'sector' | 'stage' | 'geography' | 'risk';
  constraints?: {
    min?: number;
    max?: number;
    preferred?: number;
  };
}

interface FundStrategyBuilderProps {
  portfolioState: PortfolioState;
  onUpdate: (updates: Partial<PortfolioState>) => void;
  isCalculating: boolean;
}

const defaultBuckets: AllocationBucket[] = [
  {
    id: 'enterprise-software',
    name: 'Enterprise Software',
    targetPercentage: 35,
    allocatedAmount: 17500000,
    color: '#2563eb',
    category: 'sector',
    constraints: { min: 25, max: 45, preferred: 35 }
  },
  {
    id: 'fintech',
    name: 'FinTech',
    targetPercentage: 25,
    allocatedAmount: 12500000,
    color: '#dc2626',
    category: 'sector',
    constraints: { min: 15, max: 35, preferred: 25 }
  },
  {
    id: 'healthcare-tech',
    name: 'Healthcare Tech',
    targetPercentage: 20,
    allocatedAmount: 10000000,
    color: '#059669',
    category: 'sector',
    constraints: { min: 10, max: 30, preferred: 20 }
  },
  {
    id: 'early-stage',
    name: 'Early Stage (Seed/A)',
    targetPercentage: 45,
    allocatedAmount: 22500000,
    color: '#7c3aed',
    category: 'stage',
    constraints: { min: 35, max: 55, preferred: 45 }
  },
  {
    id: 'growth-stage',
    name: 'Growth Stage (B+)',
    targetPercentage: 35,
    allocatedAmount: 17500000,
    color: '#ea580c',
    category: 'stage',
    constraints: { min: 25, max: 45, preferred: 35 }
  }
];

// Removed hardcoded COLORS - now using getChartColor() from chart-theme

export function FundStrategyBuilder({
  portfolioState,
  onUpdate,
  // eslint-disable-next-line unused-imports/no-unused-vars
  isCalculating
}: FundStrategyBuilderProps) {
  const [buckets, setBuckets] = useState<AllocationBucket[]>(defaultBuckets);
  const [selectedCategory, setSelectedCategory] = useState<'sector' | 'stage' | 'geography' | 'risk'>('sector');
  // eslint-disable-next-line unused-imports/no-unused-vars
  const [draggedIndex, setDraggedIndex] = useState<number | null>(null);

  const filteredBuckets = buckets.filter(bucket => bucket.category === selectedCategory);
  const totalAllocated = filteredBuckets.reduce((sum, bucket) => sum + bucket.targetPercentage, 0);
  const isOverAllocated = totalAllocated > 100;

  const handleDragEnd = useCallback((result: DropResult) => {
    setDraggedIndex(null);

    // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
    if (!result.destination) return;

    const items = Array.from(filteredBuckets);
    // eslint-disable-next-line @typescript-eslint/no-unsafe-argument, @typescript-eslint/no-unsafe-member-access
    const [reorderedItem] = items.splice(result.source.index, 1);
    // eslint-disable-next-line @typescript-eslint/no-unsafe-argument, @typescript-eslint/no-unsafe-member-access
    items.splice(result.destination.index, 0, reorderedItem!);

    const newBuckets = buckets.map(bucket => {
      const updatedItem = items.find(item => item.id === bucket.id);
      return updatedItem || bucket;
    });

    setBuckets(newBuckets);
  }, [buckets, filteredBuckets]);

  const updateBucketPercentage = (bucketId: string, percentage: number) => {
    setBuckets(prev => prev.map(bucket =>
      bucket.id === bucketId
        ? {
            ...bucket,
            targetPercentage: percentage,
            allocatedAmount: (portfolioState.totalFundSize * percentage) / 100
          }
        : bucket
    ));

    // Update portfolio state
    const newAllocated = buckets.reduce((sum, bucket) =>
      sum + (bucket.id === bucketId ? (portfolioState.totalFundSize * percentage) / 100 : bucket.allocatedAmount), 0
    );
    onUpdate({ allocatedCapital: newAllocated });
  };

  const addNewBucket = () => {
    const newBucket: AllocationBucket = {
      id: `bucket-${Date.now()}`,
      name: `New ${selectedCategory}`,
      targetPercentage: 0,
      allocatedAmount: 0,
      color: getChartColor(buckets.length),
      category: selectedCategory ?? 'Strategy'
    };
    setBuckets(prev => [...prev, newBucket]);
  };

  const removeBucket = (bucketId: string) => {
    setBuckets(prev => prev.filter(bucket => bucket.id !== bucketId));
  };

  const chartData = filteredBuckets.map(bucket => ({
    name: bucket.name,
    value: bucket.targetPercentage,
    color: bucket.color,
    amount: bucket.allocatedAmount
  }));

  const CustomTooltip = ({ active, payload }: {active?: boolean; payload?: Array<{payload: {name: string; value: number; amount: number}}>}) => {
    if (active && payload && payload.length) {
      const data = payload[0].payload;
      return (
        <div className="bg-white p-3 border border-gray-200 rounded-lg shadow-lg">
          <p className="font-medium text-gray-900">{data.name}</p>
          <p className="text-sm text-gray-600">
            Allocation: <span className="font-medium">{data.value.toFixed(1)}%</span>
          </p>
          <p className="text-sm text-gray-600">
            Amount: <span className="font-medium">${(data.amount / 1000000).toFixed(1)}M</span>
          </p>
        </div>
      );
    }
    return null;
  };

  return (
    <div className="space-y-6">
      {/* Category Selection */}
      <div className="flex flex-wrap gap-2">
        {(['sector', 'stage', 'geography', 'risk'] as const).map(category => (
          <Button
            key={category}
            variant={selectedCategory === category ? "default" : "outline"}
            onClick={() => setSelectedCategory(category)}
            className="capitalize"
          >
            {category}
          </Button>
        ))}
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
        {/* Allocation Controls */}
        <Card>
          <CardHeader className="flex flex-row items-center justify-between">
            <CardTitle className="text-lg flex items-center">
              <Target className="w-5 h-5 mr-2" />
              {selectedCategory} Allocation
            </CardTitle>
            <div className="flex items-center space-x-2">
              <Badge
                variant={isOverAllocated ? "destructive" : totalAllocated === 100 ? "default" : "secondary"}
                className="flex items-center"
              >
                {isOverAllocated && <AlertCircle className="w-3 h-3 mr-1" />}
                {totalAllocated.toFixed(1)}%
              </Badge>
              <Button
                size="sm"
                variant="outline"
                onClick={addNewBucket}
                className="flex items-center"
              >
                <Plus className="w-4 h-4 mr-1" />
                Add
              </Button>
            </div>
          </CardHeader>

          <CardContent className="space-y-4">
            <DragDropContext onDragEnd={handleDragEnd}>
              <Droppable droppableId="buckets">
                {(provided, snapshot) => (
                  <div
                    // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                    {...provided.droppableProps}
                    // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access
                    ref={provided.innerRef}
                    className={cn(
                      "space-y-3 min-h-[200px] p-2 rounded-lg transition-colors",
                      // eslint-disable-next-line @typescript-eslint/no-unsafe-argument, @typescript-eslint/no-unsafe-member-access
                      snapshot.isDraggingOver && "bg-blue-50"
                    )}
                  >
                    {filteredBuckets.map((bucket, index) => (
                      <Draggable key={bucket.id} draggableId={bucket.id} index={index}>
                        {(provided, snapshot) => (
                          <div
                            // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access
                            ref={provided.innerRef}
                            // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                            {...provided.draggableProps}
                            className={cn(
                              "bg-white border border-gray-200 rounded-lg p-4 transition-all",
                              // eslint-disable-next-line @typescript-eslint/no-unsafe-argument, @typescript-eslint/no-unsafe-member-access
                              snapshot.isDragging && "shadow-lg border-blue-300"
                            )}
                          >
                            <div className="flex items-center space-x-3">
                              <div
                                // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                                {...provided.dragHandleProps}
                                className="text-gray-400 hover:text-gray-600 cursor-grab"
                              >
                                <GripVertical className="w-4 h-4" />
                              </div>

                              <div
                                className="w-4 h-4 rounded-full"
                                style={{ backgroundColor: bucket.color }}
                              />

                              <div className="flex-1 space-y-2">
                                <div className="flex items-center justify-between">
                                  <Input
                                    value={bucket.name}
                                    onChange={(e) => setBuckets(prev =>
                                      prev.map(b =>
                                        b.id === bucket.id
                                          ? { ...b, name: e.target.value }
                                          : b
                                      )
                                    )}
                                    className="font-medium border-none p-0 h-auto focus-visible:ring-0"
                                  />

                                  <Button
                                    size="sm"
                                    variant="ghost"
                                    onClick={() => removeBucket(bucket.id)}
                                    className="text-red-500 hover:text-red-700 h-6 w-6 p-0"
                                  >
                                    <Trash2 className="w-3 h-3" />
                                  </Button>
                                </div>

                                <div className="space-y-2">
                                  <div className="flex items-center justify-between text-sm">
                                    <span>Target Allocation</span>
                                    <span className="font-medium">
                                      {bucket.targetPercentage.toFixed(1)}% (${(bucket.allocatedAmount / 1000000).toFixed(1)}M)
                                    </span>
                                  </div>

                                  <Slider
                                    value={[bucket.targetPercentage]}
                                    onValueChange={([value]) => updateBucketPercentage(bucket.id, value ?? 0)}
                                    max={100}
                                    step={0.5}
                                    className="w-full"
                                  />

                                  {bucket.constraints && (
                                    <div className="flex items-center space-x-2 text-xs text-gray-500">
                                      <span>Constraints:</span>
                                      <Badge variant="outline" className="text-xs">
                                        Min: {bucket.constraints.min}%
                                      </Badge>
                                      <Badge variant="outline" className="text-xs">
                                        Max: {bucket.constraints.max}%
                                      </Badge>
                                    </div>
                                  )}
                                </div>
                              </div>
                            </div>
                          </div>
                        )}
                      </Draggable>
                    ))}
                    // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                    // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                    // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            </DragDropContext>

            {/* Progress Bar */}
            <div className="space-y-2">
              <div className="flex items-center justify-between text-sm">
                <span>Total Allocation</span>
                <span className={cn(
                  "font-medium",
                  isOverAllocated ? "text-red-600" : totalAllocated === 100 ? "text-green-600" : "text-gray-600"
                )}>
                  {totalAllocated.toFixed(1)}%
                </span>
              </div>
              <Progress
                value={Math.min(totalAllocated, 100)}
                className={cn(
                  "w-full",
                  isOverAllocated && "bg-red-100"
                )}
              />
              {isOverAllocated && (
                <p className="text-xs text-red-600 flex items-center">
                  <AlertCircle className="w-3 h-3 mr-1" />
                  Over-allocated by {(totalAllocated - 100).toFixed(1)}%
                </p>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Visualization */}
        <Card>
          <CardHeader>
            <CardTitle className="text-lg flex items-center">
              <TrendingUp className="w-5 h-5 mr-2" />
              Allocation Visualization
            </CardTitle>
          </CardHeader>

          <CardContent>
            <div className="space-y-6">
              {/* Pie Chart */}
              <div className="h-64">
                <ResponsiveContainer width="100%" height="100%">
                  <PieChart>
                    <Pie
                      data={chartData}
                      cx="50%"
                      cy="50%"
                      innerRadius={60}
                      outerRadius={100}
                      paddingAngle={2}
                      dataKey="value"
                    >
                      {chartData.map((entry, index) => (
                        <Cell key={`cell-${index}`} fill={entry.color} />
                      ))}
                    </Pie>
                    <Tooltip content={<CustomTooltip />} />
                  </PieChart>
                </ResponsiveContainer>
              </div>

              {/* Bar Chart */}
              <div className="h-48">
                <ResponsiveContainer width="100%" height="100%">
                  <BarChart data={chartData} margin={{ top: 20, right: 30, left: 20, bottom: 5 }}>
                    <CartesianGrid strokeDasharray="3 3" />
                    <XAxis
                      dataKey="name"
                      angle={-45}
                      textAnchor="end"
                      height={60}
                      fontSize={12}
                    />
                    <YAxis
                      label={{ value: 'Allocation %', angle: -90, position: 'insideLeft' }}
                      fontSize={12}
                    />
                    <Tooltip content={<CustomTooltip />} />
                    <Bar dataKey="value" fill="#3B82F6" />
                  </BarChart>
                </ResponsiveContainer>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Summary Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
        <Card className="p-4">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-blue-100 rounded-lg">
              <DollarSign className="w-5 h-5 text-blue-600" />
            </div>
            <div>
              <p className="text-sm text-gray-600">Total Allocated</p>
              <p className="text-2xl font-bold">
                ${(filteredBuckets.reduce((sum, b) => sum + b.allocatedAmount, 0) / 1000000).toFixed(1)}M
              </p>
            </div>
          </div>
        </Card>

        <Card className="p-4">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-green-100 rounded-lg">
              <Target className="w-5 h-5 text-green-600" />
            </div>
            <div>
              <p className="text-sm text-gray-600">Buckets Defined</p>
              <p className="text-2xl font-bold">{filteredBuckets.length}</p>
            </div>
          </div>
        </Card>

        <Card className="p-4">
          <div className="flex items-center space-x-3">
            <div className="p-2 bg-purple-100 rounded-lg">
              <TrendingUp className="w-5 h-5 text-purple-600" />
            </div>
            <div>
              <p className="text-sm text-gray-600">Allocation Status</p>
              <p className="text-2xl font-bold text-gray-900">
                {totalAllocated === 100 ? 'Complete' : isOverAllocated ? 'Over' : 'Partial'}
              </p>
            </div>
          </div>
        </Card>
      </div>
    </div>
  );
}

import React, { useState, useMemo } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Input } from "@/components/ui/input";
import { Label } from "@/components/ui/label";
import { Badge } from "@/components/ui/badge";
import { Slider } from "@/components/ui/slider";
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from "@/components/ui/select";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  RadarChart,
  PolarGrid,
  PolarAngleAxis,
  PolarRadiusAxis,
  Radar,
  Legend
} from 'recharts';
import {
  Copy,
  Trash2,
  Plus
} from "lucide-react";
import { cn } from "@/lib/utils";
import { spreadIfDefined } from '@/lib/ts/spreadIfDefined';
import type { PortfolioState } from "@/pages/portfolio-constructor";

interface Scenario {
  id: string;
  name: string;
  description: string;
  parameters: {
    deploymentSpeed: number; // 0-100%
    riskTolerance: number; // 1-10
    sectorFocus: string;
    reserveRatio: number; // 0-1
    targetReturns: number; // multiple
    exitTimeline: number; // years
  };
  projections: {
    irr: number;
    multiple: number;
    riskScore: number;
    timeToFullDeployment: number;
    probabilityOfSuccess: number;
  };
  color: string;
  isBaseline?: boolean;
}

interface ScenarioComparisonProps {
  portfolioState: PortfolioState;
  onUpdate: (updates: Partial<PortfolioState>) => void;
  isCalculating: boolean;
}

const defaultScenarios: Scenario[] = [
  {
    id: 'baseline',
    name: 'Current Strategy',
    description: 'Current portfolio allocation and strategy',
    parameters: {
      deploymentSpeed: 75,
      riskTolerance: 6,
      sectorFocus: 'balanced',
      reserveRatio: 0.35,
      targetReturns: 2.8,
      exitTimeline: 5
    },
    projections: {
      irr: 0.22,
      multiple: 2.8,
      riskScore: 6.2,
      timeToFullDeployment: 3.2,
      probabilityOfSuccess: 0.72
    },
    color: '#2563eb',
    isBaseline: true
  },
  {
    id: 'aggressive',
    name: 'Aggressive Growth',
    description: 'Higher risk, faster deployment, growth-focused',
    parameters: {
      deploymentSpeed: 90,
      riskTolerance: 8,
      sectorFocus: 'growth',
      reserveRatio: 0.25,
      targetReturns: 3.5,
      exitTimeline: 4
    },
    projections: {
      irr: 0.28,
      multiple: 3.2,
      riskScore: 8.1,
      timeToFullDeployment: 2.1,
      probabilityOfSuccess: 0.58
    },
    color: '#dc2626'
  },
  {
    id: 'conservative',
    name: 'Conservative Approach',
    description: 'Lower risk, steady deployment, value-focused',
    parameters: {
      deploymentSpeed: 55,
      riskTolerance: 4,
      sectorFocus: 'value',
      reserveRatio: 0.45,
      targetReturns: 2.2,
      exitTimeline: 7
    },
    projections: {
      irr: 0.18,
      multiple: 2.4,
      riskScore: 4.3,
      timeToFullDeployment: 4.8,
      probabilityOfSuccess: 0.85
    },
    color: '#059669'
  }
];

const sectorFocusOptions = [
  { value: 'balanced', label: 'Balanced Portfolio' },
  { value: 'growth', label: 'Growth Focus' },
  { value: 'value', label: 'Value Focus' },
  { value: 'sector-specific', label: 'Sector Specific' },
  { value: 'stage-agnostic', label: 'Stage Agnostic' }
];

export function ScenarioComparison({
  // eslint-disable-next-line unused-imports/no-unused-vars
  portfolioState,
  onUpdate,
  // eslint-disable-next-line unused-imports/no-unused-vars
  isCalculating
}: ScenarioComparisonProps) {
  const [scenarios, setScenarios] = useState<Scenario[]>(defaultScenarios);
  const [selectedScenario, setSelectedScenario] = useState<string>('baseline');
  const [comparisonMetric, setComparisonMetric] = useState<'irr' | 'multiple' | 'risk' | 'timeline'>('irr');

  const selectedScenarioData = scenarios.find(s => s.id === selectedScenario);

  // Generate time series data for comparison
  const timeSeriesData = useMemo(() => {
    const years = Array.from({ length: 8 }, (_, i) => i);

    return years.map(year => {
      const dataPoint = { year: `Y${year}` };

      scenarios.forEach(scenario => {
        // Simulate different growth curves based on scenario parameters
        const baseGrowth = Math.pow(1 + scenario.projections.irr, year);
        const volatility = (scenario.projections.riskScore / 10) * 0.1;
        const randomness = (Math.random() - 0.5) * volatility;

        dataPoint[scenario.name] = Math.max(0, baseGrowth + randomness);
      });

      return dataPoint;
    });
  }, [scenarios]);

  // Generate radar chart data
  const radarData = useMemo(() => {
    const metrics = [
      { metric: 'IRR', field: 'irr', max: 0.35 },
      { metric: 'Multiple', field: 'multiple', max: 4 },
      { metric: 'Risk (inv)', field: 'riskScore', max: 10, invert: true },
      { metric: 'Success Prob', field: 'probabilityOfSuccess', max: 1 },
      { metric: 'Speed', field: 'timeToFullDeployment', max: 6, invert: true }
    ];

    return metrics.map(({ metric, field, max, invert }) => {
      const dataPoint = { metric };

      scenarios.forEach(scenario => {
        let value = (scenario.projections as Record<string, unknown>)[field];
        if (invert) {
          value = max - value;
        }
        dataPoint[scenario.name] = (value / max) * 100;
      });

      return dataPoint;
    });
  }, [scenarios]);

  const addNewScenario = () => {
    const newScenario: Scenario = {
      id: `scenario-${Date.now()}`,
      name: `New Scenario`,
      description: 'Custom scenario',
      parameters: {
        deploymentSpeed: 70,
        riskTolerance: 6,
        sectorFocus: 'balanced',
        reserveRatio: 0.35,
        targetReturns: 2.5,
        exitTimeline: 5
      },
      projections: {
        irr: 0.20,
        multiple: 2.5,
        riskScore: 6.0,
        timeToFullDeployment: 3.5,
        probabilityOfSuccess: 0.70
      },
      color: '#7c3aed'
    };
    setScenarios(prev => [...prev, newScenario]);
  };

  const duplicateScenario = (scenarioId: string) => {
    const scenario = scenarios.find(s => s.id === scenarioId);
    if (!scenario) return;

    const newScenario = {
      ...scenario,
      id: `scenario-${Date.now()}`,
      name: `${scenario.name} Copy`,
      isBaseline: false
    };
    setScenarios(prev => [...prev, newScenario]);
  };

  const removeScenario = (scenarioId: string) => {
    setScenarios(prev => prev.filter(s => s.id !== scenarioId));
    if (selectedScenario === scenarioId) {
      setSelectedScenario(scenarios[0]?.id || '');
    }
  };

  const updateScenarioParameter = (scenarioId: string, parameter: string, value: unknown) => {
    setScenarios(prev => prev.map(scenario => {
      if (scenario.id !== scenarioId) return scenario;

      const updatedScenario = {
        ...scenario,
        parameters: {
          ...scenario.parameters,
          [parameter]: value
        }
      };

      // Recalculate projections based on parameters (simplified)
      const risk = updatedScenario.parameters.riskTolerance;
      const speed = updatedScenario.parameters.deploymentSpeed / 100;
      const reserve = updatedScenario.parameters.reserveRatio;

      updatedScenario.projections = {
        irr: Math.max(0.1, Math.min(0.35, 0.15 + (risk * 0.02) + (speed * 0.05))),
        multiple: Math.max(1.5, Math.min(4.0, 2.0 + (risk * 0.15) + (speed * 0.1))),
        riskScore: risk + (speed * 2) - (reserve * 5),
        timeToFullDeployment: Math.max(1, 6 - speed * 4),
        probabilityOfSuccess: Math.max(0.3, Math.min(0.9, 0.8 - (risk * 0.05) + (reserve * 0.3)))
      };

      return updatedScenario;
    }));
  };

  const applyScenario = (scenarioId: string) => {
    const scenario = scenarios.find(s => s.id === scenarioId);
    if (!scenario) return;

    onUpdate({
      reserveRatio: scenario.parameters.reserveRatio,
      projectedIRR: scenario.projections.irr,
      targetReturns: scenario.projections.multiple,
      riskScore: scenario.projections.riskScore
    });
  };

  const CustomTooltip = ({ active, payload, label }) => {
    // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
    if (active && payload && payload.length) {
      return (
        <div className="bg-white p-3 border border-gray-200 rounded-lg shadow-lg">
          <p className="font-medium text-gray-900">{label}</p>
          // eslint-disable-next-line @typescript-eslint/no-unsafe-call, @typescript-eslint/no-unsafe-member-access
          // eslint-disable-next-line @typescript-eslint/no-unsafe-call, @typescript-eslint/no-unsafe-member-access
          // eslint-disable-next-line @typescript-eslint/no-unsafe-call, @typescript-eslint/no-unsafe-member-access
          {payload.map((entry, index: number) => (
            // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access
            <p key={index} className="text-sm" style={{ color: entry.color }}>
              // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access, @typescript-eslint/no-unsafe-call
              // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access, @typescript-eslint/no-unsafe-call
              // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access, @typescript-eslint/no-unsafe-call
              {entry.dataKey}: <span className="font-medium">{entry.value.toFixed(2)}x</span>
            </p>
          ))}
        </div>
      );
    }
    return null;
  };

  return (
    <div className="space-y-6">
      {/* Header Controls */}
      <div className="flex flex-col md:flex-row md:items-center justify-between space-y-4 md:space-y-0">
        <div>
          <h3 className="text-lg font-semibold">Scenario Analysis</h3>
          <p className="text-sm text-gray-600">Compare different strategic approaches</p>
        </div>

        <div className="flex items-center space-x-3">
          <Select value={comparisonMetric} onValueChange={(value) => setComparisonMetric(value)}>
            <SelectTrigger className="w-40">
              <SelectValue placeholder="Compare by..." />
            </SelectTrigger>
            <SelectContent>
              <SelectItem value="irr">IRR</SelectItem>
              <SelectItem value="multiple">Multiple</SelectItem>
              <SelectItem value="risk">Risk Score</SelectItem>
              <SelectItem value="timeline">Timeline</SelectItem>
            </SelectContent>
          </Select>

          <Button
            variant="outline"
            onClick={addNewScenario}
            className="flex items-center"
          >
            <Plus className="w-4 h-4 mr-2" />
            Add Scenario
          </Button>
        </div>
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* Scenario List */}
        <Card className="lg:col-span-1">
          <CardHeader>
            <CardTitle className="text-lg">Scenarios</CardTitle>
          </CardHeader>
          <CardContent className="space-y-3">
            {scenarios.map(scenario => (
              <div
                key={scenario.id}
                className={cn(
                  "p-3 border rounded-lg cursor-pointer transition-all hover:shadow-md",
                  selectedScenario === scenario.id ? "border-blue-500 bg-blue-50" : "border-gray-200"
                )}
                onClick={() => setSelectedScenario(scenario.id)}
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div
                      className="w-3 h-3 rounded-full"
                      style={{ backgroundColor: scenario.color }}
                    />
                    <div>
                      <p className="font-medium text-sm">{scenario.name}</p>
                      <p className="text-xs text-gray-500">{scenario.description}</p>
                    </div>
                  </div>

                  {scenario.isBaseline && (
                    <Badge variant="secondary" className="text-xs">
                      Baseline
                    </Badge>
                  )}
                </div>

                <div className="mt-2 grid grid-cols-2 gap-2 text-xs">
                  <div>
                    <span className="text-gray-500">IRR:</span>
                    <span className="ml-1 font-medium">{(scenario.projections.irr * 100).toFixed(1)}%</span>
                  </div>
                  <div>
                    <span className="text-gray-500">Multiple:</span>
                    <span className="ml-1 font-medium">{scenario.projections.multiple.toFixed(1)}x</span>
                  </div>
                  <div>
                    <span className="text-gray-500">Risk:</span>
                    <span className="ml-1 font-medium">{scenario.projections.riskScore.toFixed(1)}</span>
                  </div>
                  <div>
                    <span className="text-gray-500">Success:</span>
                    <span className="ml-1 font-medium">{(scenario.projections.probabilityOfSuccess * 100).toFixed(0)}%</span>
                  </div>
                </div>

                <div className="mt-3 flex justify-end space-x-1">
                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={(e) => {
                      e.stopPropagation();
                      duplicateScenario(scenario.id);
                    }}
                    className="h-6 w-6 p-0"
                  >
                    <Copy className="w-3 h-3" />
                  </Button>

                  {!scenario.isBaseline && (
                    <Button
                      size="sm"
                      variant="ghost"
                      onClick={(e) => {
                        e.stopPropagation();
                        removeScenario(scenario.id);
                      }}
                      className="h-6 w-6 p-0 text-red-500 hover:text-red-700"
                    >
                      <Trash2 className="w-3 h-3" />
                    </Button>
                  )}

                  <Button
                    size="sm"
                    variant="ghost"
                    onClick={(e) => {
                      e.stopPropagation();
                      applyScenario(scenario.id);
                    }}
                    className="h-6 text-xs px-2"
