// Chart UI components with shadcn/ui styling
export const ChartTooltip = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof Tooltip> &
    React.ComponentProps<"div"> & {
      hideLabel?: boolean
      hideIndicator?: boolean
      indicator?: "line" | "dot" | "dashed"
      nameKey?: string
      labelKey?: string
      payload?: unknown
      label?: unknown
    }
>(
  (
    {
      active,
      payload,
      className,
      indicator = "dot",
      hideLabel = false,
      hideIndicator = false,
      label,
      labelFormatter,
      labelClassName,
      formatter,
      color,
      nameKey,
      labelKey,
    },
    ref
  ) => {
    const { config } = useChart()

    const tooltipLabel = React.useMemo(() => {
      if (hideLabel || !Array.isArray(payload) || payload.length === 0) {
        return null
      }

      // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
      const [item] = payload
      // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
      const key = `${labelKey || item.dataKey || item.name || "value"}`
      const itemConfig = getPayloadConfigFromPayload(config, item, key)
      const value =
        !labelKey && typeof label === "string"
          ? config[label as keyof typeof config]?.label || label
          : itemConfig?.label

      if (labelFormatter) {
        return (
          // eslint-disable-next-line @typescript-eslint/no-unsafe-argument
          <div className={cn("font-medium", labelClassName)}>
            // eslint-disable-next-line @typescript-eslint/no-unsafe-call
            // eslint-disable-next-line @typescript-eslint/no-unsafe-call
            // eslint-disable-next-line @typescript-eslint/no-unsafe-call
            {labelFormatter(value, payload)}
          </div>
        )
      }

      if (!value) {
        return null
      }

      // eslint-disable-next-line @typescript-eslint/no-unsafe-argument
      return <div className={cn("font-medium", labelClassName)}>{value}</div>
    }, [
      label,
      labelFormatter,
      payload,
      hideLabel,
      labelClassName,
      config,
      labelKey,
    ])

    if (!active || !Array.isArray(payload) || payload.length === 0) {
      return null
    }

    const nestLabel = payload.length === 1 && indicator !== "dot"

    return (
      <div
        ref={ref}
        className={cn(
          "grid min-w-[8rem] items-start gap-1.5 rounded-lg border border-border/50 bg-background px-2.5 py-1.5 text-xs shadow-xl",
          // eslint-disable-next-line @typescript-eslint/no-unsafe-argument
          className
        )}
      >
        {!nestLabel ? tooltipLabel : null}
        <div className="grid gap-1.5">
          {payload.map((item, index) => {
            // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
            const key = `${nameKey || item.name || item.dataKey || "value"}`
            const itemConfig = getPayloadConfigFromPayload(config, item, key)
            // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access
            const indicatorColor = color || item.fill || item.color

            // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
            return formatter && item.value !== null ? (
              <div
                // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access
                key={item.dataKey}
                className={cn(
                  "[&>svg]:text-muted-foreground",
                  nestLabel && "items-center",
                  "flex w-full items-center justify-between gap-2"
                )}
              >
                // eslint-disable-next-line @typescript-eslint/no-unsafe-call, @typescript-eslint/no-unsafe-member-access
                // eslint-disable-next-line @typescript-eslint/no-unsafe-call, @typescript-eslint/no-unsafe-member-access
                // eslint-disable-next-line @typescript-eslint/no-unsafe-call, @typescript-eslint/no-unsafe-member-access
                {formatter(item.value, item.name, item, index, payload)}
              </div>
            ) : (
              <div
                // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access
                key={item.dataKey}
                className={cn(
                  "flex w-full items-center justify-between gap-2",
                  "[&>svg]:text-muted-foreground"
                )}
              >
                <div
                  className={cn(
                    "flex items-center gap-1.5",
                    nestLabel && "w-full justify-between"
                  )}
                >
                  {!hideIndicator && (
                    <div
                      className={cn(
                        "h-2.5 w-2.5 shrink-0 rounded-[2px] border-[--color-border] bg-[--color-bg]",
                        indicator === "dot" && "rounded-full",
                        indicator === "line" && "w-1",
                        indicator === "dashed" && "w-0 border-[1.5px] border-dashed"
                      )}
                      style={
                        {
                          // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
                          "--color-bg": indicatorColor,
                          // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
                          "--color-border": indicatorColor,
                        } as React.CSSProperties
                      }
                    />
                  )}
                  <div
                    className={cn(
                      "flex flex-1 items-baseline gap-2 [&>span]:truncate",
                      nestLabel && "justify-between text-right"
                    )}
                  >
                    <span className="text-muted-foreground">
                      // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                      // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                      // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
                      {itemConfig?.label || item.name}
                    </span>
                    {nestLabel ? <span className="font-mono font-medium text-foreground">{tooltipLabel}</span> : null}
                  </div>
                </div>
                <span className="font-mono font-medium text-right text-foreground">
                  // eslint-disable-next-line @typescript-eslint/no-unsafe-call, @typescript-eslint/no-unsafe-member-access
                  // eslint-disable-next-line @typescript-eslint/no-unsafe-call, @typescript-eslint/no-unsafe-member-access
                  // eslint-disable-next-line @typescript-eslint/no-unsafe-call, @typescript-eslint/no-unsafe-member-access
                  {item.value?.toLocaleString()}
                </span>
              </div>
            )
          })}
        </div>
      </div>
    )
  }
)

export const ChartTooltipContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<typeof ChartTooltip> & {
    hideLabel?: boolean
    hideIndicator?: boolean
    indicator?: "line" | "dot" | "dashed"
    nameKey?: string
    labelKey?: string
    config?: ChartConfig
  }
>((props, ref) => {
  const { config } = useChart()
  // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
  const { className, hideLabel, hideIndicator, indicator, nameKey, labelKey, ...rest } = props

  // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
  if (!props.active || !props.payload?.length) {
    return null
  }

  return (
    <ChartTooltip
      ref={ref}
      // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
      className={className}
      // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
      hideLabel={hideLabel}
      // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
      hideIndicator={hideIndicator}
      // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
      indicator={indicator}
      // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
      nameKey={nameKey}
      // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
      labelKey={labelKey}
      config={config}
      {...rest}
    />
  )
})

ChartTooltipContent.displayName = "ChartTooltipContent"

export const ChartLegend = React.forwardRef<
  HTMLDivElement,
  Omit<React.ComponentProps<typeof Legend>, "content"> & {
    content?: React.ComponentType<unknown> | React.ReactElement;
    className?: string;
    nameKey?: string;
  }
// eslint-disable-next-line unused-imports/no-unused-vars
>(({ className, nameKey, content, ...props }, ref) => {
  // eslint-disable-next-line unused-imports/no-unused-vars
  const { config } = useChart()

  // Filter out undefined values to satisfy exactOptionalPropertyTypes
  const filteredProps: Record<string, unknown> = Object.fromEntries(
    Object.entries(props).filter(([_, value]) => value !== undefined)
  );

  return (
    <Legend
      ref={ref as unknown as React.Ref<SVGElement>}
      className={cn(
        "flex flex-wrap items-center justify-center gap-4 text-xs",
        // eslint-disable-next-line @typescript-eslint/no-unsafe-argument
        className
      )}
      content={ChartLegendContent as unknown as React.ComponentType<unknown>}
      {...filteredProps}
      // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
      {...(nameKey && { nameKey })}
    />
  )
})

ChartLegend.displayName = "ChartLegend"

export const ChartLegendContent = React.forwardRef<
  HTMLDivElement,
  React.ComponentProps<"div"> & {
    payload?: unknown
    verticalAlign?: "top" | "bottom"
    hideIcon?: boolean
    nameKey?: string
  }
>(({ className, hideIcon = false, payload, verticalAlign = "bottom", nameKey }, ref) => {
  const { config } = useChart()

  if (!Array.isArray(payload) || payload.length === 0) {
    return null
  }

  return (
    <div
      ref={ref}
      className={cn(
        "flex items-center justify-center gap-4 text-xs",
        verticalAlign === "top" && "pb-4",
        verticalAlign === "bottom" && "pt-4",
        className
      )}
    >
      {payload.map((item) => {
        // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
        const key = `${nameKey || item.dataKey || "value"}`
        const itemConfig = getPayloadConfigFromPayload(config, item, key)

        return (
          // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access
          <div key={item.value} className="flex items-center gap-1.5">
            {!hideIcon && (
              <div
                className="h-2 w-2 rounded-[2px] bg-[--color-bg]"
                style={{
                  // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment, @typescript-eslint/no-unsafe-member-access
                  "--color-bg": item.color || itemConfig?.color,
                } as React.CSSProperties}
              />
            )}
            <span className="max-w-[100px] truncate">
              // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
              // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
              // eslint-disable-next-line @typescript-eslint/no-unsafe-member-access
              {itemConfig?.label || item.value}
            </span>
          </div>
        )
      })}
    </div>
  )
})

ChartLegendContent.displayName = "ChartLegendContent"

// Component dispatcher for lazy loading
const componentMap = {
  ChartTooltip,
  ChartTooltipContent,
  ChartLegend,
  ChartLegendContent,
  ResponsiveContainer,
  // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
  Tooltip,
  // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
  Legend
};

const RechartsDispatcher = React.forwardRef<HTMLDivElement, { component: string; [key: string]: unknown }>((props, ref) => {
  const { component, ...rest } = props;
  // eslint-disable-next-line @typescript-eslint/no-unsafe-assignment
  const Component = componentMap[component as keyof typeof componentMap];

  if (!Component) {
    return null;
  }

  return <Component ref={ref} {...(rest as Record<string, unknown>)} />;
});

export default RechartsDispatcher;

