              </div>

              {/* Expense List */}
              <div className="space-y-4">
                <h3 className="text-lg font-inter font-bold text-[#292929]">Current Expenses</h3>
                {fundExpenses.length === 0 ? (
                  <Alert>
                    <AlertCircle className="h-4 w-4" />
                    <AlertDescription>
